package Tema2;

public class EjercicioX {

	//13
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		
		for (int i=1; i<=n; i++) {
			for (int j=1; j<=i; j++) {
				System.out.print("*");
			}
			System.out.println();
		}
		
		//Espacios
		
		
	//14
		for (int i=1; i<=n; i++) {
			for(int j=1; j<=i; j--) {
				System.out.print("*");
			}
				
			//ASTERISCOS
			for (int j=1; j<=n-1; j++) {
				System.out.println("");
			}
			
	
		}
		
		
		
		
	}

}
